import cv2import torchimport pytesseractfrom ultralytics import YOLOmodel = YOLO("yolov8n.pt")cap = cv2.VideoCapture(0)while True:    ret, frame = cap.read()    if not ret:        break    results = model(frame)[0]        for box in results.boxes:       cls_id = int(box.cls[0])       label = model.names[cls_id]       conf = float(box.conf[0])       x1, y1, x2, y2 = map(int, box.xyxy[0])       if label in ['book']:           cropped = frame[y1:y2, x1:x2]                gray = cv2.cvtColor(cropped, cv2.COLOR_BGR2GRAY)           _, binarized = cv2.threshold(gray, 128, 255, cv2.THRESH_BINARY | cv2.THRESH_OTSU)           text = pytesseract.image_to_string(binarized)           # ocr_text = pytesseract.image_to_string(cropped)           print(f"[{label}] OCR Result:\n{text}\n")    # Get predictions (xyxy format)    # labels = [results[0].names[int(cls)] for cls in results[0].boxes.cls]    # print("Detected: ", labels)            cv2.imshow("camera image", frame)    if cv2.waitKey(1) & 0xFF == ord('q'):        breakcap.release()cv2.destroyAllWindows()